defs.script:
function nsResolver() {
  return 'http://www.w3.org/2000/svg'
}
// this function display element with xml:lang or systemLanguage equals lang, it's use a xml:lang attribute to save the language of the element
function selectSystemLang(lang) {
 try {
 var a = new Array();
 // find elements with systemLanguage
 var i = document.evaluate("//svg:*[@systemLanguage!='' or @xml:lang!='']", document,nsResolver, XPathResult.ORDERED_NODE_ITERATOR_TYPE, null);
  var n = i.iterateNext();
  while (n) {
      if (n.parentNode.id!="SVGSwitchLang") a.push(n);
     n = i.iterateNext();
  }
  // the displayed systemLanguage value
  var cl=SVGSwitchLang();
  for (var i=0;i < a.length;i++) {
    var n = a[i];
    var v;
    // the language of the element 
    if (n.hasAttribute("xml:lang")) v = n.getAttribute("xml:lang");
    else v = n.getAttribute("systemLanguage");
    // set to displayed value if equals lang, other set it to invisible value
    if (v == lang) n.setAttribute("systemLanguage",cl);
    else n.setAttribute("systemLanguage",'_'+v);
    // save lang  element value
     if (!n.hasAttribute("xml:lang")) n.setAttribute("xml:lang",v);
  }
  } catch(ex) {alert("selectSystemLang("+lang+") "+ex);}
}
// this function identify the displayed systemLanguage 
function SVGSwitchLang() {
var l = 'ab,aa,af,sq,am,ar,an,hy,as,ay,az,ba,eu,bn,dz,bh,bi,br,bg,my,be,km,ca,zh,zh-Hans,zh-Hant,co,hr,cs,da,nl,en,eo,et,fo,fa,fj,fi,fr,fy,gl,gd,gv,ka,de,el,kl,gn,gu,ht,ha,he,iw,hi,hu,is,io,id,in,ia,ie,iu,ik,ga,it,ja,jv,kn,ks,kk,rw,ky,rn,ko,ku,lo,la,lv,li,ln,lt,mk,mg,ms,ml,mt,mi,mr,mo,mn,na,ne,no,oc,or,om,ps,pl,pt,pa,qu,rm,ro,ru,sm,sg,sa,sr,sh,st,tn,sn,ii,sd,si,ss,sk,sl,so,es,su,sw,sv,tl,tg,ta,tt,te,th,bo,ti,to,ts,tr,tk,tw,ug,uk,ur,uz,vi,vo,wa,cy,wo,xh,yi,ji,yo,zu'.split(',');
var s = document.getElementById('SVGSwitchLang');
var x = parseInt(s.getBBox().x,10);
//alert("x="+s.getBBox().x+" "+x+" "+l[x]);
if (x >=0 && x < l.length) return l[x];
else throw "SVGSwitchLang: unexpected value '"+x+"'";
};

var languageSelectors = {
	highlight : function(lang) {
		var g = document.getElementById("languageSelectors");
		var id=lang+"_selector";
		for (var n=g.firstChild;n!=null;n=n.nextSibling) {
			if (n.tagName && n.tagName=="text") {
				if (n.id==id) n.style.fill="#000000";
				else n.style.fill="#b8b8b8";
			}
		}
	},
	select : function(lang) {
		try {
		selectSystemLang(lang);
		languageSelectors.highlight(lang);
		} catch(ex) {alert("select("+lang+") exception "+ex);}
	}
};
// display current language
languageSelectors.highlight(SVGSwitchLang());
